import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';

export default function NavBar() {
  const navigate = useNavigate();

  const handleLogoClick = () => {
    navigate('/');
  };

  const handleCompassClick = () => {
    navigate('/compass');
  };

  return (
    <nav className="fixed top-0 left-0 right-0 z-50 glass-card border-b border-cyan-400/20 m-4">
      <div className="flex items-center justify-between px-8 py-6">
        {/* Logo */}
        <div className="flex items-center gap-6">
          <motion.button
            onClick={handleLogoClick}
            className="text-5xl font-bold text-cyan-400 hover:text-white transition-all duration-300 glow-text"
            aria-label="Go to Home"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
          >
            ‚àû INFINITY
          </motion.button>
          <div className="hidden lg:block text-xl text-gray-300 font-light">
            THE SINGULARITY OF SPORTS CARD INTELLIGENCE
          </div>
        </div>

        {/* Navigation Links */}
        <div className="flex items-center gap-2">
          <NavLink 
            to="/" 
            className={({ isActive }) => isActive ? 'nav-active' : 'nav'}
            aria-label="Home"
          >
            Home
          </NavLink>

          <NavLink 
            to="/grader" 
            className={({ isActive }) => isActive ? 'nav-active' : 'nav'}
            aria-label="AI Grader"
          >
            üî¨ AI Grader
          </NavLink>

          <NavLink 
            to="/market" 
            className={({ isActive }) => isActive ? 'nav-active' : 'nav'}
            aria-label="Market Scanner"
          >
            üìà Market Scan
          </NavLink>

          <NavLink 
            to="/oracle" 
            className={({ isActive }) => isActive ? 'nav-active' : 'nav'}
            aria-label="AI Oracle"
          >
            üåå Oracle
          </NavLink>

          <NavLink 
            to="/portfolio" 
            className={({ isActive }) => isActive ? 'nav-active' : 'nav'}
            aria-label="Portfolio"
          >
            üíº Portfolio
          </NavLink>

          <NavLink 
            to="/analytics" 
            className={({ isActive }) => isActive ? 'nav-active' : 'nav'}
            aria-label="Analytics"
          >
            üìä Analytics
          </NavLink>

          <NavLink 
            to="/settings" 
            className={({ isActive }) => isActive ? 'nav-active' : 'nav'}
            aria-label="Settings"
          >
            ‚öôÔ∏è Settings
          </NavLink>

          <button 
            onClick={handleCompassClick} 
            className="btn"
            aria-label="Investment Compass"
          >
            üß≠ Compass
          </button>
        </div>
      </div>
    </nav>
  );
}
